DROP TABLE IF EXISTS `cms_ad`;
CREATE TABLE `cms_ad` (
  `ad_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '广告位ID',
  `ad_code` varchar(50) DEFAULT NULL COMMENT '广告位编码',
  `ad_name` varchar(150) DEFAULT NULL COMMENT '广告位名称',
  `width` smallint(6) DEFAULT NULL COMMENT '宽度',
  `height` smallint(6) DEFAULT NULL COMMENT '高度',
  `status` smallint(6) DEFAULT NULL COMMENT '状态',
  `user_id` varchar(50) DEFAULT NULL COMMENT '创建人ID',
  `dept_id` varchar(50) DEFAULT NULL COMMENT '部门ID',
  `description` varchar(255) DEFAULT NULL COMMENT '描述',
  `create_by` varchar(50) DEFAULT NULL COMMENT '创建人',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`ad_id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_ad
-- ----------------------------

-- ----------------------------
-- Table structure for cms_ad_material
-- ----------------------------
DROP TABLE IF EXISTS `cms_ad_material`;
CREATE TABLE `cms_ad_material` (
  `id` bigint(11) NOT NULL AUTO_INCREMENT,
  `ad_id` varchar(50) DEFAULT NULL COMMENT '广告位ID',
  `material_id` varchar(50) DEFAULT NULL COMMENT '广告素材ID',
  `link` varchar(255) DEFAULT NULL COMMENT '链接',
  `sort` smallint(6) DEFAULT '255' COMMENT '排序',
  `hit` int(11) DEFAULT '0' COMMENT '点击数',
  `start_time` datetime DEFAULT NULL COMMENT '开始时间',
  `end_time` datetime DEFAULT NULL COMMENT '结束时间',
  `status` smallint(6) DEFAULT NULL COMMENT '状态',
  `use_his_id` int(11) DEFAULT NULL COMMENT '使用记录表ID',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_ad_material
-- ----------------------------

-- ----------------------------
-- Table structure for cms_album
-- ----------------------------
DROP TABLE IF EXISTS `cms_album`;
CREATE TABLE `cms_album` (
  `album_id` varchar(50) NOT NULL COMMENT '相册ID',
  `album_name` varchar(150) DEFAULT NULL COMMENT '相册名称',
  `user_id` varchar(50) DEFAULT NULL COMMENT '创建人ID',
  `dept_id` varchar(50) DEFAULT NULL COMMENT '部门ID',
  `album_type` varchar(50) DEFAULT NULL COMMENT '相册类型',
  `cover_image` varchar(255) DEFAULT NULL COMMENT '封面图片',
  `description` varchar(255) DEFAULT NULL COMMENT '描述',
  `create_by` varchar(50) DEFAULT NULL COMMENT '创建人',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `audit_state` smallint(6) DEFAULT '0' COMMENT '审核状态',
  `code` varchar(50) DEFAULT NULL COMMENT '编码',
  `width` smallint(6) DEFAULT NULL COMMENT '宽度',
  `height` smallint(6) DEFAULT NULL COMMENT '高度',
  `hit` int(11) DEFAULT '0' COMMENT '点击数',
  `up_vote` int(11) DEFAULT '0' COMMENT '点赞数',
  `comment_flag` smallint(6) DEFAULT '0' COMMENT '是否开启评论',
  PRIMARY KEY (`album_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_album
-- ----------------------------

-- ----------------------------
-- Table structure for cms_album_material
-- ----------------------------
DROP TABLE IF EXISTS `cms_album_material`;
CREATE TABLE `cms_album_material` (
  `id` bigint(11) NOT NULL AUTO_INCREMENT,
  `album_id` varchar(50) DEFAULT NULL COMMENT '专辑ID',
  `material_id` varchar(50) DEFAULT NULL COMMENT '素材ID',
  `link` varchar(255) DEFAULT NULL COMMENT '链接',
  `sort` smallint(6) DEFAULT NULL COMMENT '排序',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=210 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_album_material
-- ----------------------------

-- ----------------------------
-- Table structure for cms_article
-- ----------------------------
DROP TABLE IF EXISTS `cms_article`;
CREATE TABLE `cms_article` (
  `id` int(50) NOT NULL AUTO_INCREMENT COMMENT '主键，文章ID',
  `yhid` varchar(50) DEFAULT NULL COMMENT '作者',
  `article_region` varchar(50) DEFAULT '1' COMMENT '推荐到哪个专区。比如：头条、精选、最新、热门、评论最多等',
  `author` varchar(50) DEFAULT NULL COMMENT '原文作者',
  `publish_time` varchar(50) DEFAULT NULL COMMENT '发布时间',
  `title` varchar(255) DEFAULT NULL COMMENT '文章标题',
  `keywords` varchar(255) DEFAULT NULL COMMENT '关键词',
  `description` varchar(255) DEFAULT NULL COMMENT '摘要',
  `cover_image` varchar(255) DEFAULT NULL COMMENT '封面图片',
  `article_model` varchar(50) DEFAULT NULL COMMENT '文章模型',
  `copy_flag` smallint(6) DEFAULT '0' COMMENT '转载标志',
  `category_id` varchar(50) DEFAULT NULL COMMENT '频道栏目ID',
  `personal_category` varchar(255) DEFAULT NULL COMMENT '个人分类，多个用逗号分隔',
  `link` varchar(255) DEFAULT NULL COMMENT '原始链接',
  `static_url` varchar(255) DEFAULT NULL COMMENT '静态化后url',
  `tags` varchar(100) DEFAULT NULL COMMENT '标签',
  `hit` int(11) DEFAULT '0' COMMENT '点击数',
  `reply_num` int(11) DEFAULT '0' COMMENT '回复数',
  `up_vote` int(11) DEFAULT '0' COMMENT '点赞数',
  `down_vote` int(11) DEFAULT '0' COMMENT '差评数',
  `hot_flag` smallint(6) DEFAULT '0' COMMENT '热点标志',
  `new_flag` smallint(6) DEFAULT '0' COMMENT '新增标志',
  `comment_flag` smallint(6) DEFAULT '0' COMMENT '是否开启评论',
  `top_flag` smallint(6) DEFAULT '0' COMMENT '置顶标志',
  `favourite` int(11) DEFAULT '0' COMMENT '收藏数',
  `mission_id` varchar(50) DEFAULT NULL COMMENT '趴取任务的ID',
  `template_name` varchar(50) DEFAULT NULL COMMENT '生成静态页面的模板(cms_template表中的name)',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `available` smallint(6) DEFAULT '1' COMMENT '状态标志',
  `deleted` smallint(6) DEFAULT '0' COMMENT '删除标志',
  `extra1` varchar(255) DEFAULT NULL COMMENT '附加字段1',
  `extra2` varchar(255) DEFAULT NULL COMMENT '附加字段2',
  `extra3` varchar(255) DEFAULT NULL COMMENT '跳转链接',
  `sort` int(255) DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `index_title` (`title`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=9183 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_article
-- ----------------------------

-- ----------------------------
-- Table structure for cms_article_content
-- ----------------------------
DROP TABLE IF EXISTS `cms_article_content`;
CREATE TABLE `cms_article_content` (
  `id` varchar(50) CHARACTER SET latin1 NOT NULL,
  `content` longtext,
  `content_markdown_source` longtext,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_article_content
-- ----------------------------

-- ----------------------------
-- Table structure for cms_article_template
-- ----------------------------
DROP TABLE IF EXISTS `cms_article_template`;
CREATE TABLE `cms_article_template` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `name` varchar(255) DEFAULT NULL COMMENT '文章模板名称',
  `tags` varchar(255) DEFAULT NULL COMMENT '标签',
  `user_id` varchar(50) DEFAULT NULL COMMENT '用户ID',
  `user_name` varchar(50) DEFAULT NULL COMMENT '用户名称',
  `content` text COMMENT '内容',
  `share_type` varchar(50) DEFAULT NULL COMMENT '共享类型',
  `weight` int(11) DEFAULT NULL COMMENT '权重',
  `hot_falg` smallint(6) DEFAULT NULL COMMENT '最热',
  `new_flag` smallint(6) DEFAULT NULL COMMENT '最新',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `audit` smallint(6) DEFAULT NULL COMMENT '审核标志',
  `audit_time` datetime DEFAULT NULL COMMENT '审核时间',
  `audit_by` varchar(50) DEFAULT NULL COMMENT '审核人',
  `audit_name` varchar(50) DEFAULT NULL COMMENT '审核人名称',
  `audit_reason` varchar(255) DEFAULT NULL COMMENT '原因',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=46 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_article_template
-- ----------------------------

-- ----------------------------
-- Table structure for cms_attachment
-- ----------------------------
DROP TABLE IF EXISTS `cms_attachment`;
CREATE TABLE `cms_attachment` (
  `attach_id` varchar(50) NOT NULL COMMENT 'id',
  `zid` varchar(50) DEFAULT NULL COMMENT '组ID',
  `user_id` varchar(50) DEFAULT NULL,
  `file_type` varchar(50) NOT NULL COMMENT '文件类型，引用cms_filetype表ID',
  `file_name` varchar(500) DEFAULT NULL COMMENT '文件名称',
  `file_path` varchar(500) DEFAULT NULL COMMENT '相对路径',
  `file_url` varchar(500) DEFAULT NULL COMMENT 'url',
  `size` int(11) DEFAULT NULL COMMENT '文件空间',
  `create_by` varchar(50) DEFAULT NULL COMMENT '用户ID',
  `create_time` datetime DEFAULT NULL,
  `sort` smallint(6) DEFAULT NULL COMMENT '排序',
  PRIMARY KEY (`attach_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_attachment
-- ----------------------------

-- ----------------------------
-- Table structure for cms_category
-- ----------------------------
DROP TABLE IF EXISTS `cms_category`;
CREATE TABLE `cms_category` (
  `category_id` bigint(50) NOT NULL AUTO_INCREMENT COMMENT '分类ID',
  `category_name` varchar(50) DEFAULT NULL COMMENT '分类名称',
  `parent_id` bigint(50) DEFAULT NULL COMMENT '父ID',
  `ancestors` varchar(50) DEFAULT NULL COMMENT 'ids',
  `sort` smallint(6) DEFAULT NULL COMMENT '排序',
  `description` varchar(200) DEFAULT NULL COMMENT '分类描述',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `create_by` varchar(50) DEFAULT NULL COMMENT '创建人',
  `update_by` varchar(50) DEFAULT NULL COMMENT '更新人',
  `status` tinyint(4) DEFAULT '0' COMMENT '状态（0正常 1停用）',
  `del_flag` tinyint(4) DEFAULT NULL COMMENT '删除标志',
  PRIMARY KEY (`category_id`)
) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_category
-- ----------------------------

-- ----------------------------
-- Table structure for cms_comment
-- ----------------------------
DROP TABLE IF EXISTS `cms_comment`;
CREATE TABLE `cms_comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `pid` int(11) DEFAULT NULL COMMENT '父ID',
  `tid` varchar(50) DEFAULT NULL COMMENT '目标ID',
  `num_pei` smallint(6) DEFAULT '0' COMMENT '呸数',
  `num_penzi` smallint(6) DEFAULT '0' COMMENT '喷子数',
  `num_dou` smallint(6) DEFAULT '0' COMMENT '逗个数',
  `num_geili` smallint(6) DEFAULT '0' COMMENT '给力数',
  `type` varchar(50) DEFAULT 'article' COMMENT '评论类型',
  `user_id` varchar(50) DEFAULT NULL COMMENT '用户id',
  `user_name` varchar(50) DEFAULT NULL COMMENT '用户名称',
  `avatar` varchar(255) DEFAULT NULL COMMENT '用户头像',
  `content` varchar(255) DEFAULT NULL COMMENT '评论内容',
  `up_vote` int(11) DEFAULT '0' COMMENT '点赞数',
  `down_vote` int(11) DEFAULT '0' COMMENT '反对数',
  `qq` varchar(20) DEFAULT NULL COMMENT 'QQ',
  `email` varchar(30) DEFAULT NULL COMMENT '邮箱',
  `ip` varchar(20) DEFAULT NULL COMMENT 'IP',
  `address` varchar(255) DEFAULT NULL COMMENT '地址',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `status` smallint(6) DEFAULT '0' COMMENT '状态0正常',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=86 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_comment
-- ----------------------------

-- ----------------------------
-- Table structure for cms_email
-- ----------------------------
DROP TABLE IF EXISTS `cms_email`;
CREATE TABLE `cms_email` (
  `id` varchar(50) NOT NULL COMMENT 'ID',
  `email_type` smallint(6) DEFAULT '0' COMMENT '邮件类型',
  `user_id` varchar(50) DEFAULT NULL COMMENT '用户ID',
  `create_by` varchar(50) DEFAULT NULL,
  `create_time` datetime NOT NULL,
  `from_email` varchar(50) DEFAULT NULL COMMENT '发送者邮箱',
  `from_email_pwd` varchar(50) DEFAULT NULL COMMENT '发送者邮箱密码',
  `to_email` varchar(50) DEFAULT NULL COMMENT '接收者邮箱',
  `subject` varchar(255) DEFAULT NULL COMMENT '主题',
  `content` text COMMENT '内容',
  `send_flag` smallint(1) DEFAULT '0' COMMENT '是否发送',
  `send_time` datetime DEFAULT NULL COMMENT '发送时间',
  `send_type` smallint(10) DEFAULT '0' COMMENT '发送类型 0立即 1定时',
  `plan_time` datetime DEFAULT NULL COMMENT '定时时间',
  `copy_to` varchar(255) DEFAULT NULL COMMENT '抄送用户',
  `attachfiles` varchar(255) DEFAULT NULL COMMENT '附件',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_email
-- ----------------------------

-- ----------------------------
-- Table structure for cms_filetype
-- ----------------------------
DROP TABLE IF EXISTS `cms_filetype`;
CREATE TABLE `cms_filetype` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `suffix` varchar(50) CHARACTER SET utf8 DEFAULT NULL COMMENT '后缀',
  `name` varchar(50) CHARACTER SET utf8 DEFAULT NULL COMMENT '中文名称',
  `icon` varchar(255) CHARACTER SET utf8 DEFAULT NULL COMMENT '图标',
  PRIMARY KEY (`id`),
  UNIQUE KEY `unique_suffix` (`suffix`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=25 DEFAULT CHARSET=latin1;

-- ----------------------------
-- Records of cms_filetype
-- ----------------------------

-- ----------------------------
-- Table structure for cms_friend_link
-- ----------------------------
DROP TABLE IF EXISTS `cms_friend_link`;
CREATE TABLE `cms_friend_link` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '友情链接ID',
  `name` varchar(50) DEFAULT NULL COMMENT '友链名称',
  `link` varchar(255) DEFAULT NULL COMMENT '链接',
  `description` varchar(255) DEFAULT NULL COMMENT '站点描述',
  `logo` varchar(255) DEFAULT NULL COMMENT 'logo素材',
  `email` varchar(255) DEFAULT NULL COMMENT '邮箱',
  `audit_state` smallint(6) DEFAULT '0' COMMENT '状态 0待审核 1通过 2未通过',
  `status` smallint(6) DEFAULT '0' COMMENT '状态 0正常 1停用',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_friend_link
-- ----------------------------

-- ----------------------------
-- Table structure for cms_link
-- ----------------------------
DROP TABLE IF EXISTS `cms_link`;
CREATE TABLE `cms_link` (
  `link_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '链接ID',
  `link_type` varchar(50) DEFAULT NULL COMMENT '链接分类',
  `link_name` varchar(255) DEFAULT NULL COMMENT '链接名称',
  `keywords` varchar(100) DEFAULT NULL COMMENT '关键词',
  `link` varchar(255) DEFAULT NULL COMMENT '链接',
  `description` varchar(255) DEFAULT NULL COMMENT '描述',
  `logo` varchar(255) DEFAULT NULL COMMENT 'Logo',
  `audit_state` smallint(6) DEFAULT '0' COMMENT '审核状态',
  `detail` longtext COMMENT '详情',
  `sort` int(11) DEFAULT '0' COMMENT '排序',
  `up_vote` int(11) DEFAULT '0' COMMENT '点赞数',
  `comment_flag` smallint(6) DEFAULT '1' COMMENT '允许评论',
  `status` smallint(6) DEFAULT '0' COMMENT '状态 0正常 1停用',
  PRIMARY KEY (`link_id`)
) ENGINE=InnoDB AUTO_INCREMENT=19 DEFAULT CHARSET=utf8;

