-- Records of cms_link
-- ----------------------------

-- ----------------------------
-- Table structure for cms_link_type
-- ----------------------------
DROP TABLE IF EXISTS `cms_link_type`;
CREATE TABLE `cms_link_type` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `link_type` varchar(100) DEFAULT NULL COMMENT '链接分类',
  `link_type_name` varchar(100) DEFAULT NULL COMMENT '链接分类名称',
  `description` varchar(255) DEFAULT NULL COMMENT '描述',
  `cover_image` varchar(255) DEFAULT NULL COMMENT '封面图片',
  `sort` smallint(6) DEFAULT '0' COMMENT '排序',
  `status` smallint(6) DEFAULT '0' COMMENT '状态 0正常 1停用',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

