-- Records of cms_link_type
-- ----------------------------

-- ----------------------------
-- Table structure for cms_material
-- ----------------------------
DROP TABLE IF EXISTS `cms_material`;
CREATE TABLE `cms_material` (
  `material_id` varchar(64) NOT NULL,
  `group_id` varchar(64) NOT NULL DEFAULT '0' COMMENT '分组id',
  `material_name` varchar(100) NOT NULL COMMENT '素材名称',
  `material_type` varchar(10) NOT NULL COMMENT '素材类型 字典 1图片2视频3文本',
  `description` varchar(255) DEFAULT NULL COMMENT '素材描述',
  `material_size` varchar(20) DEFAULT NULL COMMENT '素材大小',
  `save_path` varchar(255) DEFAULT NULL COMMENT '保存路径',
  `thumbnail` longtext COMMENT '缩略图',
  `audit_state` varchar(20) DEFAULT '0' COMMENT '审核状态 0待审批2未通过1通过',
  `audit_reason` varchar(255) DEFAULT NULL COMMENT '审核意见',
  `use_state` varchar(20) DEFAULT '1' COMMENT '可用状态 0停用1启用',
  `width` varchar(255) DEFAULT NULL COMMENT '分辨率 宽',
  `height` varchar(255) DEFAULT NULL COMMENT '分辨率 高',
  `uploader_id` varchar(64) DEFAULT NULL COMMENT '上传者 id',
  `upload_time` datetime DEFAULT NULL COMMENT '上传日期',
  `remark` varchar(255) DEFAULT NULL COMMENT '备注',
  PRIMARY KEY (`material_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_material
-- ----------------------------

-- ----------------------------
-- Table structure for cms_material_group
-- ----------------------------
DROP TABLE IF EXISTS `cms_material_group`;
CREATE TABLE `cms_material_group` (
  `group_id` bigint(50) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `parent_id` bigint(50) DEFAULT NULL COMMENT '父ID',
  `dept_id` varchar(50) DEFAULT NULL COMMENT '部门ID',
  `group_name` varchar(50) DEFAULT NULL COMMENT '分组名称',
  `description` varchar(255) DEFAULT NULL COMMENT '描述',
  `sort` smallint(6) DEFAULT NULL COMMENT '排序',
  `user_id` varchar(50) DEFAULT NULL COMMENT '创建人用户ID',
  `create_by` varchar(50) DEFAULT NULL COMMENT '创建人',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`group_id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_material_group
-- ----------------------------

-- ----------------------------
-- Table structure for cms_material_use
-- ----------------------------
DROP TABLE IF EXISTS `cms_material_use`;
CREATE TABLE `cms_material_use` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `material_id` varchar(50) DEFAULT NULL COMMENT '素材ID',
  `use_table` varchar(50) DEFAULT NULL COMMENT '表名',
  `use_id` varchar(50) DEFAULT NULL COMMENT '比如cms_album_material表的album_id字段',
  `use_column` varchar(50) DEFAULT NULL COMMENT '比如cms_album_material表的album_id字段的列名“album_id”',
  `user_id` varchar(50) DEFAULT NULL COMMENT '创建人ID',
  `create_by` varchar(50) DEFAULT NULL COMMENT '创建人名称',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=202 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_material_use
-- ----------------------------

-- ----------------------------
-- Table structure for cms_resource
-- ----------------------------
DROP TABLE IF EXISTS `cms_resource`;
CREATE TABLE `cms_resource` (
  `id` varchar(64) NOT NULL COMMENT '资源ID',
  `resource_region` varchar(50) DEFAULT NULL COMMENT '资源专区',
  `title` varchar(100) NOT NULL COMMENT '资源名称',
  `type` varchar(10) NOT NULL COMMENT '资源类型',
  `description` varchar(255) DEFAULT NULL COMMENT '资源描述',
  `detail` longtext COMMENT '详情',
  `size` varchar(20) DEFAULT NULL COMMENT '资源大小',
  `save_path` varchar(255) DEFAULT NULL COMMENT '保存路径',
  `cover_image` varchar(255) DEFAULT NULL COMMENT '缩略图',
  `audit_state` varchar(20) DEFAULT '0' COMMENT '审核状态 0待审批2未通过1通过',
  `score` smallint(6) DEFAULT NULL COMMENT '评分 1-5',
  `download_type` varchar(20) DEFAULT '0' COMMENT '下载类型 0直接下载 1登录后下载 2关注下载',
  `favourite_count` int(11) DEFAULT '0' COMMENT '收藏数',
  `download_count` int(11) DEFAULT '0' COMMENT '下载次数',
  `hit` int(11) DEFAULT '0' COMMENT '点击数',
  `up_vote` int(11) DEFAULT '0' COMMENT '点赞数',
  `free` smallint(6) DEFAULT NULL COMMENT '付费 0免费 1收费',
  `pay_type` varchar(50) DEFAULT NULL COMMENT '付费类型 1 积分 2扫码支付',
  `cost` int(11) DEFAULT '0' COMMENT '花费多少积分',
  `tag_ids` varchar(255) DEFAULT NULL COMMENT '标签',
  `user_id` varchar(50) DEFAULT NULL COMMENT '上传人用户ID',
  `user_name` varchar(255) DEFAULT NULL COMMENT '上传人',
  `create_time` datetime DEFAULT NULL COMMENT '上传日期',
  `update_time` datetime DEFAULT NULL COMMENT '上传者 id',
  `status` smallint(6) DEFAULT '0' COMMENT '状态 0 正常 1停用',
  `remark` varchar(255) DEFAULT NULL COMMENT '备注',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_resource
-- ----------------------------

-- ----------------------------
-- Table structure for cms_short_words
-- ----------------------------
DROP TABLE IF EXISTS `cms_short_words`;
CREATE TABLE `cms_short_words` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `short_words` varchar(255) DEFAULT '' COMMENT '短语',
  `tagIds` varchar(100) DEFAULT NULL COMMENT '标签',
  `user_id` varchar(20) DEFAULT NULL COMMENT '用户ID',
  `words_count` int(11) DEFAULT NULL COMMENT '字数',
  `weight` int(11) DEFAULT '0' COMMENT '权重',
  `audit_state` smallint(6) DEFAULT NULL COMMENT '审核状态',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

