-- Records of cms_short_words
-- ----------------------------

-- ----------------------------
-- Table structure for cms_site_msg
-- ----------------------------
DROP TABLE IF EXISTS `cms_site_msg`;
CREATE TABLE `cms_site_msg` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `from_id` varchar(50) DEFAULT NULL COMMENT '发送者ID',
  `from_name` varchar(50) DEFAULT NULL COMMENT '发送者名称',
  `to_id` varchar(50) DEFAULT NULL COMMENT '接受者ID',
  `to_name` varchar(50) DEFAULT NULL COMMENT '接受者名称',
  `msg_type` varchar(50) DEFAULT NULL COMMENT '消息类别',
  `content` text COMMENT '消息内容',
  `read_flag` smallint(6) DEFAULT '0' COMMENT '0未读1已读',
  `create_time` datetime DEFAULT NULL COMMENT '消息时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

